// Process webhook data and prepare for workflow
const webhookData = $input.first().json;

// Handle array payload - extract first item if it's an array
let bodyData;
if (Array.isArray(webhookData)) {
  bodyData = webhookData[0].body;
} else if (webhookData.body) {
  bodyData = webhookData.body;
} else {
  bodyData = webhookData;
}

console.log('=== PROCESSING NEW WEBHOOK DATA ===');
console.log('Blog Request ID:', bodyData.blogRequestId);
console.log('Blog Post ID:', bodyData.blogPostId);
console.log('Blog Topic:', bodyData.blogTopic);
console.log('Input Type:', bodyData.inputType);
console.log('Focused Keywords:', bodyData.focusedKeywords);
console.log('Content Goal:', bodyData.contentGoal);
console.log('Additional Context:', bodyData.additionalContext);

// Process the data based on input type
let processedContent = '';
let inputType = bodyData.inputType;

if (inputType === 'Text') {
  processedContent = bodyData.blogTopic;
  if (bodyData.additionalContext) {
    processedContent += '\n\nAdditional Context: ' + bodyData.additionalContext;
  }
} else if (inputType === 'URL') {
  // URL will be processed by URL content extraction nodes later
  processedContent = bodyData.blogTopic; // This should be the URL
} else if (inputType === 'Voice Note') {
  // Voice note will be processed by voice transcription nodes later
  processedContent = bodyData.blogTopic; // This should be the voice file path
}

// Prepare data for the workflow with new structure
const processedData = {
  // Baserow configuration
  clientId: bodyData.clientId,
  clientName: bodyData.clientName,
  baserowToken: bodyData.baserowToken,
  baserowDatabaseId: bodyData.baserowDatabaseId,
  blogRequestsTableId: bodyData.blogRequestsTableId,
  blogPostsTableId: bodyData.blogPostsTableId,
  keywordResearchTableId: bodyData.keywordResearchTableId,
  fieldMappings: bodyData.fieldMappings,

  // Blog content data
  blogTopic: bodyData.blogTopic,
  inputType: inputType,
  focusedKeywords: bodyData.focusedKeywords,
  contentGoal: bodyData.contentGoal,
  additionalContext: bodyData.additionalContext,
  processedContent: processedContent,

  // Voice note data (if applicable)
  hasVoiceNote: bodyData.hasVoiceNote,
  voiceNoteFileName: bodyData.voiceNoteFileName,
  voiceNoteFileSize: bodyData.voiceNoteFileSize,
  voiceNoteFileType: bodyData.voiceNoteFileType,

  // Record IDs
  blogRequestId: bodyData.blogRequestId,
  blogPostId: bodyData.blogPostId,

  // Timestamps and status
  timestamp: bodyData.timestamp,
  status: bodyData.status || 'processing',

  // Processing metadata
  processing_status: 'webhook_processed',
  content_ready: inputType === 'Text', // Text is immediately ready
  next_action: inputType === 'Text' ? 'process_text' : `process_${inputType.toLowerCase().replace(' ', '_')}`
};

console.log('=== PROCESSED DATA READY ===');
console.log('Blog Request ID:', processedData.blogRequestId);
console.log('Blog Post ID:', processedData.blogPostId);
console.log('Input Type:', processedData.inputType);
console.log('Processed Content Length:', processedData.processedContent?.length);
console.log('Field Mappings Available:', !!processedData.fieldMappings);

return [{ json: processedData }];

// CLEAN URL CONTENT2 - DATA PRESERVATION CHECK
const payload = $json;

console.log('=== CLEAN URL CONTENT - DATA ANALYSIS ===');
console.log('Input data keys:', Object.keys(payload));
console.log('Has extracted HTML data:', !!payload.title || !!payload.main_content);
console.log('Has clientConfig:', !!payload.clientConfig);

// CRITICAL: Get the original workflow data that started the URL processing
console.log('=== ORIGINAL DATA RECOVERY ===');
let originalWorkflowData = {};

try {
  // Try to get from URL Processor2 (the node that started URL processing)
  const urlProcessorData = $('URL Processor2').first();
  if (urlProcessorData?.json) {
    originalWorkflowData = urlProcessorData.json;
    console.log(`✅ Found original data from URL Processor2`);
    console.log('Original data has clientConfig:', !!originalWorkflowData.clientConfig);
    console.log('Original data keys:', Object.keys(originalWorkflowData));
  } else {
    console.log(`❌ No data found from URL Processor2`);
  }
} catch (error) {
  console.log(`⚠️ Error getting original data: ${error.message}`);
}

// If no original data found, try alternative recovery
if (!originalWorkflowData.clientConfig) {
  console.log('⚠️ No clientConfig in original data, attempting broader recovery...');

  try {
    // Try to get from Process Webhook Data node
    const webhookData = $('Process Webhook Data').first();
    if (webhookData?.json) {
      console.log('✅ Found data from Process Webhook Data');
      originalWorkflowData = webhookData.json;
    }
  } catch (error) {
    console.log('❌ Could not recover data from Process Webhook Data:', error.message);
  }
}

// Extract and clean the web content from the HTML extraction
const extractedData = payload;

console.log('=== CONTENT EXTRACTION ===');
console.log('Title:', extractedData.title?.substring(0, 50) + '...');
console.log('Description:', extractedData.description?.substring(0, 50) + '...');
console.log('Main content available:', !!extractedData.main_content);
console.log('Headings available:', !!extractedData.headings);

// Combine and clean the extracted content
let cleanedContent = '';

// Add title if available
if (extractedData.title && extractedData.title.trim()) {
  cleanedContent += `Title: ${extractedData.title.trim()}\n\n`;
}

// Add description if available
if (extractedData.description && extractedData.description.trim()) {
  cleanedContent += `Description: ${extractedData.description.trim()}\n\n`;
}

// Add headings if available
if (extractedData.headings && Array.isArray(extractedData.headings)) {
  const headings = extractedData.headings.filter(h => h && h.trim()).join('\n');
  if (headings) {
    cleanedContent += `Key Headings:\n${headings}\n\n`;
  }
}

// Add main content if available
if (extractedData.main_content) {
  let mainContent = '';
  if (Array.isArray(extractedData.main_content)) {
    mainContent = extractedData.main_content.filter(p => p && p.trim()).join('\n\n');
  } else {
    mainContent = extractedData.main_content.trim();
  }

  if (mainContent) {
    cleanedContent += `Main Content:\n${mainContent}`;
  }
}

// Clean up the content
cleanedContent = cleanedContent
  .replace(/\s+/g, ' ')  // Replace multiple spaces with single space
  .replace(/\n\s*\n/g, '\n\n')  // Clean up multiple newlines
  .trim();

// Limit content length (max 4000 characters for AI processing)
if (cleanedContent.length > 4000) {
  cleanedContent = cleanedContent.substring(0, 4000) + '... [content truncated]';
}

// Prepare the processed data WITH ORIGINAL WORKFLOW DATA PRESERVED
const processedData = {
  // PRESERVE ALL ORIGINAL WORKFLOW DATA FIRST
  ...originalWorkflowData,

  // Add the extracted HTML data
  ...extractedData,

  // Add processing results
  processed_content: cleanedContent || 'No content could be extracted from the URL',
  processing_status: cleanedContent ? 'completed' : 'partial',
  processing_type: 'url_extraction_completed',
  content_ready: true,

  // ENSURE CRITICAL DATA IS PRESERVED
  clientConfig: originalWorkflowData.clientConfig, // CRITICAL!
  client_data: originalWorkflowData.client_data,
  baserow_record: originalWorkflowData.baserow_record,
  hook_focus: originalWorkflowData.hook_focus,
  cta: originalWorkflowData.cta,
  content_type: originalWorkflowData.content_type || 'url',
  platforms: originalWorkflowData.platforms,
  number_of_posts: originalWorkflowData.number_of_posts,

  // Keep original identifiers
  recordId: originalWorkflowData.recordId,
  baseId: originalWorkflowData.baseId,
  clientId: originalWorkflowData.clientId,
  clientName: originalWorkflowData.clientName,

  // Extraction metadata
  extracted_data: {
    title: extractedData.title || '',
    description: extractedData.description || '',
    content_length: cleanedContent.length,
    extraction_successful: !!cleanedContent
  },

  processing_notes: `URL content extracted and cleaned. Length: ${cleanedContent.length} characters`,

  // Debug info
  debug_info: {
    ...originalWorkflowData.debug_info,
    url_processing_completed: true,
    original_data_preserved: true,
    clientConfig_preserved: !!originalWorkflowData.clientConfig,
    content_extracted: !!cleanedContent,
    processing_timestamp: new Date().toISOString()
  }
};

console.log('=== URL PROCESSING SUMMARY ===');
console.log(`Content extraction: ${cleanedContent ? '✅ SUCCESS' : '❌ FAILED'}`);
console.log(`Content length: ${cleanedContent.length} characters`);
console.log(`clientConfig preserved: ${!!processedData.clientConfig}`);
console.log(`client name: ${processedData.clientConfig?.client?.name || 'missing'}`);
console.log(`record ID: ${processedData.clientConfig?.recordId || 'missing'}`);
console.log(`brand assets table: ${processedData.clientConfig?.tables?.brandAssets?.id || 'missing'}`);
console.log('=== END URL PROCESSING ===');

return {
  json: processedData
};

// BLOG URL PROCESSOR - Process URL input for blog creation
const payload = $json;

console.log('=== BLOG URL PROCESSOR ===');
console.log('Input data keys:', Object.keys(payload));
console.log('Blog topic (URL):', payload.blogTopic);
console.log('Input type:', payload.inputType);
console.log('Focused keywords:', payload.focusedKeywords);
console.log('Content goal:', payload.contentGoal);

// Extract URL from blog topic
const targetUrl = payload.blogTopic || payload.processedContent;

console.log('Processing URL:', targetUrl);

// Validate URL
if (!targetUrl || targetUrl === '') {
  return {
    json: {
      ...payload,
      processing_status: 'error',
      error_message: 'No URL provided in blog topic',
      content_ready: false
    }
  };
}

// Basic URL validation
const urlPattern = /^https?:\/\/.+/;
if (!urlPattern.test(targetUrl)) {
  return {
    json: {
      ...payload,
      processing_status: 'error',
      error_message: 'Invalid URL format - must start with http:// or https://',
      content_ready: false
    }
  };
}

// Prepare for URL processing with blog context
const processingData = {
  ...payload,

  // URL processing data
  target_url: targetUrl,
  processing_status: 'ready_for_extraction',
  processing_type: 'url_extraction',
  content_ready: false,
  next_action: 'fetch_and_extract',
  processing_notes: `URL ready for content extraction: ${targetUrl}`,

  // Blog-specific data
  blog_topic: payload.blogTopic,
  focused_keywords: payload.focusedKeywords,
  content_goal: payload.contentGoal,
  additional_context: payload.additionalContext,

  // Debug info
  debug_info: {
    url_processing_started: true,
    target_url: targetUrl,
    blog_context_preserved: true
  }
};

console.log('=== BLOG URL PROCESSING RESULT ===');
console.log('Target URL:', processingData.target_url);
console.log('Blog topic:', processingData.blog_topic);
console.log('Keywords:', processingData.focused_keywords);

return {
  json: processingData
};

// BLOG TEXT PROCESSOR - Process text input for blog creation
const payload = $json;

console.log('=== BLOG TEXT PROCESSOR ===');
console.log('Input data keys:', Object.keys(payload));
console.log('Blog topic:', payload.blogTopic);
console.log('Input type:', payload.inputType);
console.log('Focused keywords:', payload.focusedKeywords);
console.log('Content goal:', payload.contentGoal);

// For text input, we use the blog topic and additional context directly
const processedData = {
  // PRESERVE ALL ORIGINAL DATA
  ...payload,

  // Processing results for blog creation
  processed_content: payload.processedContent || payload.blogTopic,
  processing_status: 'completed',
  processing_type: 'text_direct',
  content_ready: true,
  processing_notes: 'Text content ready for blog creation',

  // Blog-specific data
  blog_topic: payload.blogTopic,
  focused_keywords: payload.focusedKeywords,
  content_goal: payload.contentGoal,
  additional_context: payload.additionalContext,

  // Debug info
  debug_info: {
    text_processing_completed: true,
    blog_data_ready: true,
    content_length: (payload.processedContent || payload.blogTopic)?.length || 0
  }
};

console.log('=== BLOG TEXT PROCESSING RESULT ===');
console.log('Processed content length:', processedData.processed_content?.length);
console.log('Blog topic:', processedData.blog_topic);
console.log('Keywords:', processedData.focused_keywords);

return {
  json: processedData
};

// BLOG VOICE TRANSCRIPTION PROCESSOR - Process voice transcription for blog creation
const whisperResponse = $json;

console.log('=== BLOG VOICE TRANSCRIPTION PROCESSING ===');
console.log('Whisper Response Keys:', Object.keys(whisperResponse));
console.log('Has whisper text:', !!whisperResponse.text);

// Extract transcription from Whisper API response
let transcription = '';
let processingStatus = 'completed';
let errorMessage = '';

try {
  if (whisperResponse.text && whisperResponse.text.trim() !== '') {
    transcription = whisperResponse.text.trim();
    console.log(`✅ Transcription successful: ${transcription.length} characters`);
    console.log(`Transcription preview: ${transcription.substring(0, 100)}...`);
  } else if (whisperResponse.error) {
    errorMessage = whisperResponse.error.message || 'Unknown Whisper API error';
    transcription = `Error transcribing voice: ${errorMessage}`;
    processingStatus = 'error';
    console.log(`❌ Whisper API error: ${errorMessage}`);
  } else {
    errorMessage = 'No transcription text in response';
    transcription = 'Unable to transcribe voice - no text returned';
    processingStatus = 'error';  
    console.log(`❌ No transcription text found in response`);
  }
} catch (error) {
  errorMessage = `Processing error: ${error.message}`;
  transcription = 'Error processing transcription response';
  processingStatus = 'error';
  console.log(`❌ Processing error: ${error.message}`);
}

// Get the original blog workflow data that started the voice processing
console.log('=== ORIGINAL BLOG DATA RECOVERY ===');
let originalBlogData = {};

try {
  // Try to get from Voice Note Processor2
  const voiceProcessorData = $('Voice Note Processor2').first();
  if (voiceProcessorData?.json) {
    originalBlogData = voiceProcessorData.json;
    console.log(`✅ Found original blog data from Voice Note Processor2`);
    console.log('Original data keys:', Object.keys(originalBlogData));
    console.log('Blog topic:', originalBlogData.blog_topic);
    console.log('Keywords:', originalBlogData.focused_keywords);
  } else {
    console.log(`❌ No data found from Voice Note Processor2`);
  }
} catch (error) {
  console.log(`⚠️ Error getting original blog data: ${error.message}`);
}

// Prepare the processed blog data with transcription
const processedBlogData = {
  // PRESERVE ALL ORIGINAL BLOG DATA
  ...originalBlogData,

  // Apply transcription result  
  processed_content: transcription,
  processing_status: processingStatus,
  processing_type: 'voice_transcription_completed',
  content_ready: processingStatus === 'completed',
  voice_transcription: transcription,
  error_message: errorMessage,

  // Processing notes
  processing_notes: processingStatus === 'completed' 
    ? `✅ Voice transcribed for blog creation. Length: ${transcription.length} chars`
    : `❌ Voice transcription failed for blog: ${errorMessage}`,

  // Debug info
  debug_info: {
    ...originalBlogData.debug_info,
    voice_transcription_completed: true,
    original_blog_data_preserved: true,
    transcription_length: transcription.length,
    processing_timestamp: new Date().toISOString()
  }
};

console.log('=== BLOG VOICE TRANSCRIPTION SUMMARY ===');
console.log(`Transcription Status: ${processingStatus === 'completed' ? '✅ SUCCESS' : '❌ FAILED'}`);
console.log(`Transcription Length: ${transcription.length} characters`);
console.log(`Blog Topic: ${processedBlogData.blog_topic}`);
console.log(`Keywords: ${processedBlogData.focused_keywords}`);
console.log(`Content Goal: ${processedBlogData.content_goal}`);
console.log('=== END BLOG TRANSCRIPTION SUMMARY ===');

// Return single blog item with transcription applied
return {
  json: processedBlogData
};

// BLOG VOICE NOTE PROCESSOR - Process voice input for blog creation
const payload = $json;

console.log('=== BLOG VOICE NOTE PROCESSOR ===');
console.log('Input data keys:', Object.keys(payload));
console.log('Blog topic (voice file):', payload.blogTopic);
console.log('Input type:', payload.inputType);
console.log('Focused keywords:', payload.focusedKeywords);
console.log('Content goal:', payload.contentGoal);
console.log('Has voice note:', payload.hasVoiceNote);
console.log('Voice file name:', payload.voiceNoteFileName);

// Extract voice file URL from blog topic or voice note data
let voiceFileUrl = payload.blogTopic || payload.processedContent;

// If we have voice note data, use that
if (payload.hasVoiceNote && payload.voiceNoteFileName) {
  // Construct voice file URL from the file name
  voiceFileUrl = `/uploads/voice-notes/${payload.voiceNoteFileName}`;
}

console.log('Processing Voice Note:', voiceFileUrl);

// Validate voice file URL
if (!voiceFileUrl || voiceFileUrl === '') {
  return {
    json: {
      ...payload,
      processing_status: 'error',
      error_message: 'No voice file URL provided in blog topic',
      content_ready: false
    }
  };
}

// Prepare for voice processing with blog context
const processingData = {
  ...payload,

  // Voice processing data
  voice_file_url: voiceFileUrl,
  processing_status: 'ready_for_transcription',
  processing_type: 'voice_transcription',
  content_ready: false,
  next_action: 'download_and_transcribe',
  processing_notes: `Voice file ready for transcription: ${voiceFileUrl}`,

  // Blog-specific data
  blog_topic: payload.blogTopic,
  focused_keywords: payload.focusedKeywords,
  content_goal: payload.contentGoal,
  additional_context: payload.additionalContext,

  // Voice note data
  has_voice_note: payload.hasVoiceNote,
  voice_file_name: payload.voiceNoteFileName,
  voice_file_size: payload.voiceNoteFileSize,
  voice_file_type: payload.voiceNoteFileType,

  // Debug info
  debug_info: {
    voice_processing_started: true,
    voice_file_url: voiceFileUrl,
    blog_context_preserved: true
  }
};

console.log('=== BLOG VOICE PROCESSING RESULT ===');
console.log('Voice file URL:', processingData.voice_file_url);
console.log('Blog topic:', processingData.blog_topic);
console.log('Keywords:', processingData.focused_keywords);

return {
  json: processingData
};

// BLOG INFO PROCESSOR - Prepare blog data for AI workflow
const processedItem = $json;

console.log('=== BLOG INFO PROCESSOR ===');
console.log('Input data keys:', Object.keys(processedItem));
console.log('Blog topic:', processedItem.blogTopic);
console.log('Input type:', processedItem.inputType);
console.log('Focused keywords:', processedItem.focusedKeywords);
console.log('Content goal:', processedItem.contentGoal);
console.log('Processed content:', processedItem.processed_content?.substring(0, 100) + '...');

// Extract client configuration from the original webhook data
const clientConfig = {
  clientId: processedItem.clientId,
  baserowToken: processedItem.baserowToken,
  baserowDatabaseId: processedItem.baserowDatabaseId,
  blogRequestsTableId: processedItem.blogRequestsTableId,
  blogPostsTableId: processedItem.blogPostsTableId,
  keywordResearchTableId: processedItem.keywordResearchTableId,
  fieldMappings: processedItem.fieldMappings
};

console.log('=== CLIENT CONFIG EXTRACTED ===');
console.log('Client ID:', clientConfig.clientId);
console.log('Database ID:', clientConfig.baserowDatabaseId);
console.log('Blog Posts Table:', clientConfig.blogPostsTableId);
console.log('Keyword Research Table:', clientConfig.keywordResearchTableId);

// Prepare blog data for AI workflow
const blogData = {
  // PRESERVE ALL ORIGINAL DATA
  ...processedItem,

  // Blog-specific data
  blog_topic: processedItem.blogTopic,
  input_type: processedItem.inputType,
  focused_keywords: processedItem.focusedKeywords,
  content_goal: processedItem.contentGoal,
  additional_context: processedItem.additionalContext,

  // Content data
  source_content: processedItem.processed_content || processedItem.blogTopic,
  processed_content: processedItem.processed_content || processedItem.blogTopic,

  // Client configuration
  clientConfig: clientConfig,
  client_id: clientConfig.clientId,

  // Processing status
  processing_status: 'ready_for_ai_workflow',
  processing_type: 'blog_creation',
  content_ready: true,

  // Blog workflow specific fields
  Keyword: processedItem.focusedKeywords, // For AI agents
  Title: processedItem.blogTopic, // Initial title
  Content: processedItem.processed_content || processedItem.blogTopic, // Content for AI processing

  // Debug info
  debug_info: {
    blog_info_processed: true,
    client_config_extracted: true,
    ready_for_ai_workflow: true,
    content_length: (processedItem.processed_content || processedItem.blogTopic)?.length || 0
  }
};

console.log('=== BLOG INFO PROCESSING RESULT ===');
console.log('Blog topic:', blogData.blog_topic);
console.log('Keywords:', blogData.focused_keywords);
console.log('Content goal:', blogData.content_goal);
console.log('Client ID:', blogData.client_id);
console.log('Ready for AI workflow:', blogData.content_ready);

return {
  json: blogData
};

